package com.jsp.footmap.dao;import com.jsp.footmap.model.Comment;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.springframework.stereotype.Repository;import java.util.Date;import java.util.List;@Mapper@Repository(value = "commentDAO")public interface CommentDao {    String TABLE_NAME = "comment";    String INSERT_FIELD = "sid,uid,comment,status,commentTime,reply, replyTime";    @Insert({"insert into", TABLE_NAME, "(", INSERT_FIELD, ")values(#{sid},#{uid},#{comment},#{status},#{commentTime},#{reply}, #{replyTime})" })    void addComment(Comment comment);    @Select({"select * from", TABLE_NAME, "where sid=#{sid} and status=#{status}"})    List<Comment> getComments(int sid, int status);    @Update({"update", TABLE_NAME, "set reply=#{reply}, replyTime=#{replyTime} where cid=#{cid}"})    void addReply(int cid, String reply, Date replyTime);    @Update({"update", TABLE_NAME, "set status=#{status} where cid=#{cid}"})    void updateStatus(int cid, int status);}