package com.jsp.footmap.dao;import com.jsp.footmap.model.Email;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.springframework.stereotype.Repository;@Mapper@Repository(value = "emailDAO")public interface EmailDao {    String TABLE_NAME = "email";    String INSERT_FIELD = "inputEmail, code, uid";    @Insert({"insert into",TABLE_NAME,"(",INSERT_FIELD,")values(#{inputEmail},#{code},#{uid})"})    void addEmail(Email email);    @Select({"select code from",TABLE_NAME, "where inputEmail=#{inputEmail}"})    int selectCodeByEmail(String inputEmail);    @Select({"select * from", TABLE_NAME,"where inputEmail=#{inputEmail}"})    Email selectEmail(String inputEmail);    @Update({"update", TABLE_NAME, "set code=#{code} where eid=#{eid}"})    void updateEmail(Email email);    @Update({"update", TABLE_NAME, "set uid=#{uid} where inputEmail=#{inputEmail}"})    void fixEmail(int uid, String inputEmail);    @Select({"select inputEmail from",TABLE_NAME,"where uid=#{uid}"})    String selectEmailByUid(int uid);}