package com.jsp.footmap.dao;import com.jsp.footmap.model.ResponseMap;import com.jsp.footmap.model.UserMap;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.springframework.stereotype.Repository;import java.util.Date;import java.util.List;@Mapper@Repository("mapDAO")public interface MapDao {    String TABLE_NAME = "map";    String INSERT_FIELD = "uid, currentTime, currentPlace";    @Insert({"insert into", TABLE_NAME, "(", INSERT_FIELD,") values(#{uid},#{currentTime},#{currentPlace})"})    void addMap(UserMap userMap);    @Select({"select currentTime, currentPlace from", TABLE_NAME, "where uid = #{uid} and currentTime between #{selectDate} and #{nextDate}"})    List<ResponseMap> getUserMap(int uid, Date selectDate, Date nextDate);}