package com.jsp.footmap.dao;import com.jsp.footmap.model.Record;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.springframework.stereotype.Repository;import java.util.List;@Mapper@Repository("recordDAO")public interface RecordDao {    String TABLE_NAME = "record";    String INSERT_FILED = "uid, targetTime, remarks,targetPlace";    @Insert({"insert into", TABLE_NAME, "(", INSERT_FILED ,") values (#{uid}, #{targetTime}, #{remarks}, #{targetPlace})" })    void addRecord(Record record);    @Select({"select * from", TABLE_NAME, "where uid = #{uid} and targetPlace = #{currentPlace}"})    List<Record> getRecordByUid(int uid, String currentPlace);}