package com.jsp.footmap.dao;import com.jsp.footmap.model.Record;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.springframework.stereotype.Repository;import java.util.List;@Mapper@Repository("recordDAO")public interface RecordDao {    String TABLE_NAME = "record";    String INSERT_FILED = "uid, targetTime, remarks,targetPlace, startTime, title, status";    @Insert({"insert into", TABLE_NAME, "(", INSERT_FILED ,") values (#{uid}, #{targetTime}, #{remarks}, #{targetPlace}, #{startTime}, #{title}, #{status})" })    void addRecord(Record record);    @Select({"select * from", TABLE_NAME, "where uid = #{uid} and targetPlace = #{currentPlace}"})    List<Record> getRecordByUid(int uid, String currentPlace);    @Select({"select * from", TABLE_NAME, "where uid = #{uid} and status != -1"})    List<Record> getRecordsByUid(int uid);    @Update({"update", TABLE_NAME, "set status=#{status} where rid = #{rid}"})    void updateStatus(int rid, int status);    @Update({"update", TABLE_NAME, "set title=#{title}, targetTime=#{targetTime}, remarks=#{remarks}, targetPlace=#{targetPlace}, startTime=#{startTime} where rid=#{rid}"})    void updateRecord(Record record);}