package com.jsp.footmap.dao;import com.jsp.footmap.model.HotShare;import com.jsp.footmap.model.Share;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.springframework.stereotype.Repository;import java.util.List;@Mapper@Repository("shareDAO")public interface ShareDao {    String TABLE_NAME = "share";    String INSERT_FIELD = "uid,subject,sendTime,status,contents,likeCount,disLikeCount";    @Insert({"insert into", TABLE_NAME, "(", INSERT_FIELD,")values(#{uid},#{subject},#{sendTime},#{status},#{contents},#{likeCount},#{commentCount})"})    void addShard(Share share);    @Update({"update", TABLE_NAME, "set likeCount=#{likeCount}, disLikeCount=#{disLikeCount} where sid = #{sid}"})    void updateLikeCount(int sid, int likeCount, int disLikeCount);    @Select({"select * from", TABLE_NAME, "where status = 1"})    List<Share> getShares();    @Select({"select * from", TABLE_NAME, "where sid=#{sid} and status=1"})    Share getShareBySid(int sid);    @Update({"update", TABLE_NAME, "set commentCount=commentCount+1 where sid=#{sid}"})    void addCommentCount(int sid);    @Update({"update", TABLE_NAME, "set status=#{status} where sid=#{sid}"})    void updateStatus(int sid, int status);    @Select({"select sid,subject,commentCount,likeCount from", TABLE_NAME, "where status=1 and (commentCount >= #{commentCount} or likeCount >= #{commentCount}) "})    List<HotShare> getHotShare(int commentCount);}