package com.jsp.footmap.dao;import com.jsp.footmap.model.Token;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.springframework.stereotype.Repository;@Mapper@Repository("tokenDAO")public interface TokenDao {    String TABLE_NAME = "token";    String INSERT_FIELD = "uid, expired, status, tokens";    @Insert({"insert into", TABLE_NAME, "(", INSERT_FIELD, ") values (#{uid},#{expired},#{status},#{tokens})"})    void addToken(Token token);    @Select({"select * from",TABLE_NAME, "where tokens = #{tokens}"})    Token selectByToken(String tokens);    @Update({"update", TABLE_NAME, "set status = #{status} where tokens = #{token}"})    void updateTokenStatus(String token, int status);}