package com.jsp.footmap.dao;import com.jsp.footmap.model.User;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import org.apache.ibatis.annotations.Update;import org.springframework.stereotype.Repository;import java.util.List;@Mapper@Repository(value = "userDAO")public interface UserDao {    String TABLE_NAME = "user";    String INSERT_FIELD = "username, password, nickname, status, salt, manager";    @Select({"select * from", TABLE_NAME})    List<User> findAllUser();    @Select({"select * from", TABLE_NAME, "where username=#{username}"})    User findUserByUsername(String username);    @Insert({"insert into", TABLE_NAME , "(", INSERT_FIELD, ") values (#{username},#{password}," +            "#{nickname},#{status},#{salt},#{manager})"})    void insertUser(User user);    @Select({"select uid from", TABLE_NAME, "where username=#{username}"})    int findUidByUsername(String username);    @Select({"select * from", TABLE_NAME, "where uid = #{uid}"})    User findUserById(int uid);    @Update({"update", TABLE_NAME,"set nickname=#{nickname}, password=#{password} where uid=#{uid}"})    void updateUser(User user);    @Update({"update", TABLE_NAME, "set status=#{status} where username=#{username}"})    void updateStatus(String username,int status);}