package com.jsp.footmap.model;import java.util.Date;public class Comment {    private int cid;    private int sid;    private int uid;    private String comment;    private int status;    private Date commentTime;    private String reply;    private String nickname;    private String replyTime;    public int getCid() {        return cid;    }    public void setCid(int cid) {        this.cid = cid;    }    public int getSid() {        return sid;    }    public void setSid(int sid) {        this.sid = sid;    }    public int getUid() {        return uid;    }    public void setUid(int uid) {        this.uid = uid;    }    public String getComment() {        return comment;    }    public void setComment(String comment) {        this.comment = comment;    }    public int getStatus() {        return status;    }    public void setStatus(int status) {        this.status = status;    }    public Date getCommentTime() {        return commentTime;    }    public void setCommentTime(Date commentTime) {        this.commentTime = commentTime;    }    public String getReply() {        return reply;    }    public void setReply(String reply) {        this.reply = reply;    }    public String getNickname() {        return nickname;    }    public void setNickname(String nickname) {        this.nickname = nickname;    }    public String getReplyTime() {        return replyTime;    }    public void setReplyTime(String replyTime) {        this.replyTime = replyTime;    }}