package com.jsp.footmap.model;import org.springframework.stereotype.Component;/** * 表示当前用户是谁 (存放用户) */// 该注解把普通类注入到spring容器里。泛指各种组件@Componentpublic class HostHolder {    private static ThreadLocal<User> users = new ThreadLocal<User>();    public User getUser() {        return users.get();    }    public void setUser(User user) {        users.set(user);    }    public void clear() {        users.remove();    }}