package com.jsp.footmap.model;public class HotShare {    private int sid;    private String subject;    private int commentCount;    private int likeCount;    public int getSid() {        return sid;    }    public void setSid(int sid) {        this.sid = sid;    }    public String getSubject() {        return subject;    }    public void setSubject(String subject) {        this.subject = subject;    }    public int getCommentCount() {        return commentCount;    }    public void setCommentCount(int commentCount) {        this.commentCount = commentCount;    }    public int getLikeCount() {        return likeCount;    }    public void setLikeCount(int likeCount) {        this.likeCount = likeCount;    }}