package com.jsp.footmap.model;import java.util.Date;/** * 提醒(备注)类 */public class Record {    private int rid;    private int uid;    private Date targetTime;    private String remarks;    private String targetPlace;    private Date startTime;    private int status;    private String title;    public int getRid() {        return rid;    }    public void setRid(int rid) {        this.rid = rid;    }    public int getUid() {        return uid;    }    public void setUid(int uid) {        this.uid = uid;    }    public Date getTargetTime() {        return targetTime;    }    public void setTargetTime(Date targetTime) {        this.targetTime = targetTime;    }    public String getRemarks() {        return remarks;    }    public void setRemarks(String remarks) {        this.remarks = remarks;    }    public String getTargetPlace() {        return targetPlace;    }    public void setTargetPlace(String targetPlace) {        this.targetPlace = targetPlace;    }    public Date getStartTime() {        return startTime;    }    public void setStartTime(Date startTime) {        this.startTime = startTime;    }    public int getStatus() {        return status;    }    public void setStatus(int status) {        this.status = status;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }}