package com.jsp.footmap.model;/** * 用户类 */public class User {    private int uid;    private String username;    private String password;    private String nickname;    private int status;    private String salt;    private int manager;    public int getUid() {        return uid;    }    public void setUid(int uid) {        this.uid = uid;    }    public String getUsername() {        return username;    }    public void setUsername(String username) {        this.username = username;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getNickname() {        return nickname;    }    public void setNickname(String nickname) {        this.nickname = nickname;    }    public int getStatus() {        return status;    }    public void setStatus(int status) {        this.status = status;    }    public String getSalt() {        return salt;    }    public void setSalt(String salt) {        this.salt = salt;    }    public int getManager() {        return manager;    }    public void setManager(int manager) {        this.manager = manager;    }}