package com.jsp.footmap.utils;import java.text.SimpleDateFormat;import java.util.Calendar;import java.util.Date;public class DateUtils {    /**     * String类型日期转换为Date类型     * @param s     * @return     */    public static Date StringToDate(String s) {       try {           SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");           return sdf.parse(s);       } catch (Exception e) {           e.printStackTrace();           return null;       }    }    public static Date StringToDate2(String s) {        try {            SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd");            return sdf.parse(s);        } catch (Exception e) {            e.printStackTrace();            return null;        }    }    public static Date StringToDate3(String s) {        try {            SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd HH:mm");            return sdf.parse(s);        } catch (Exception e) {            e.printStackTrace();            return null;        }    }    /**     * 获取指定日期下一天     * @param date     * @return     */    public static Date getNextDate(Date date) {        Calendar cld = Calendar.getInstance();        cld.setTime(date);        cld.add(Calendar.DATE, 1);        return cld.getTime();    }    public static Date addEightHour (Date date) {        Calendar cld = Calendar.getInstance();        cld.setTime(date);        cld.add(Calendar.HOUR, 8);        return cld.getTime();    }    public static String DateToString(Date date) {        SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd HH:mm");        return sdf.format(date);    }}