package com.jsp.footmap.utils;import org.junit.Test;import java.text.SimpleDateFormat;import java.util.Date;public class DateUtilsTest {    @Test    public void dateTest() {        Date date = DateUtils.StringToDate("2016-10-24 21:59:30");        System.out.println(date.toLocaleString());    }    @Test    public void dateTest2() {        Date date = DateUtils.StringToDate2("2016-10-24");        System.out.println(date.toLocaleString());    }    @Test    public void dateTest3() {        Date date = DateUtils.StringToDate2("2016-10-24");        Date nextDate = DateUtils.getNextDate(date);        System.out.println(nextDate + ":" + nextDate.toLocaleString());    }    @Test    public void dateTest4() {        Date date = DateUtils.StringToDate("2016-10-24 21:59:30");        System.out.println(date.toLocaleString());        Date date1 = DateUtils.addEightHour(date);        System.out.println(date1.toLocaleString());    }    @Test    public void dateTest5() {        Date date = new Date();        String s = DateUtils.DateToString(date);        System.out.println(s);    }}